from tkinter import *


def dogswindow():
    dogwindow = Toplevel(root)
    dogwindow.title("Dogs!")
    dogimage = PhotoImage(file='dogpicture.png')
    dogs = Label(dogwindow, image=(dogimage))
    dogs.pack()
    dogwindow.mainloop()
def catswindow():
    catwindow = Toplevel(root)
    catwindow.title("Cats!")
